create table IoReportStyle (id  bigserial not null, borderBottom boolean not null, borderLeft boolean not null, borderRight boolean not null, borderTop boolean not null, code varchar(255), colorBackground varchar(255), colorBorderBottom varchar(255), colorBorderLeft varchar(255), colorBorderRight varchar(255), colorBorderTop varchar(255), colorFont varchar(255), font varchar(255), fontBold boolean not null, fontItalic boolean not null, position int4 not null, sizeBorderBottom int4 not null, sizeBorderLeft int4 not null, sizeBorderRight int4 not null, sizeBorderTop int4 not null, sizeFont int4 not null, visible boolean not null, alignment_id int8, primary key (id));
create table IoReportStyleJtDescription (style_id int8 not null, description_id int8 not null, primary key (style_id, description_id));
create table IoReportStyleJtLang (style_id int8 not null, lang_id int8 not null, primary key (style_id, lang_id));